import boto3
from boto3.dynamodb.conditions import Key

class BaseRepository:

    def __init__(self, table_name, key, secondary_index=None):
        client = boto3.resource('dynamodb')
        self.table = client.Table(table_name)
        self.key = key
        self.secondary_index = secondary_index

    def get_item(self, value):
        dynamo_result = self.table.get_item(
            Key={
                self.key: value 
            }
        )

        return dynamo_result.get('Item')

    def put_item(self, item):
        self.table.put_item(Item=item)

    def get_item_by_secondary_index(self, value):
        response = self.table.query(
            IndexName=self.secondary_index + '-index',
            KeyConditionExpression=Key(self.secondary_index).eq(value)
        )
        
        if len(response['Items']) == 0:
            return None

        return response['Items'][0]
